import axios from 'axios';

export const fetchMyTodos = async () => {
    const res = await axios.get('http://localhost:8000/dev/todo/fetchMyTodos');
    console.log("Fetched data:", res.data)

    return res.data;
}

export const addTodo = async (name, _id) => {
    const res = await axios.post('http://localhost:8000/dev/todo/add', {
        _id,
        name,
    });

    return res.data;
}

export const updateTodo = async (id, name) => {
    console.log("idddddddd", id);
    console.log("name", name);
    const res = await axios.put('/dev/todo/edit', {
        _id: id,
        name,
    });

    return res.data;
}

export const deleteTodo = async (id) => {
    const res = await axios.delete(`/dev/todo/delete/${id}`);

    return res.data;
}
