import axios from 'axios';

export const fetchMyTodos = async () => {
    const res = await axios.get('http://localhost:8000/dev/todo/fetchMyTodos');
    console.log("fecthed:::::",res)
    return res.data;
}

export const addTodo = async (name, favourite) => {
    const res = await axios.post('http://localhost:8000/dev/todo/add', {
        name,
    });

    return res.data;
}

export const updateTodo = async (id, name) => {
    const res = await axios.put('/todo/edit', {
        _id: id,
        name,
    });

    return res.data;
}

export const deleteTodo = async (id) => {
    const res = await axios.delete(`/todo/delete/${id}`);

    return res.data;
}
