import axios from "axios"

export const fetchMyTodos = async () => {
    const res = await axios.get('/todo/fetchMyTodos');

    return res.data;
}

export const addTodo = async (name, favourite) => {
    const res = await axios.post('/todo/add', {
        name,
        favourite
    });

    return res.data;
}

export const updateTodo = async (id, name) => {
    const res = await axios.put('/todo/edit', {
        _id: id,
        name,
    });

    return res.data;
}

export const updateFav = async (id, favourite) => {
    const res = await axios.put('/todo/edit', {
        _id: id,
        favourite,
    });

    return res.data;
}

export const deleteTodo = async (id) => {
    const res = await axios.delete(`/todo/delete/${id}`);

    return res.data;
}
